<?php//Autocomplete auteursif (isset($_GET['term'])){	$return_arr = array();	$reqIdh = "https://api.archives-ouvertes.fr/ref/author/?q=fullName_t:".$_GET['term']."*%20AND%20valid_s:%22VALID%22&fl=fullName_s&rows=1000";	$reqIdh = str_replace(' ', '%20', $reqIdh);	$contents = file_get_contents($reqIdh);	$results = json_decode($contents);	$numFound = 0;	if (isset($results->response->numFound)) {$numFound = $results->response->numFound;}		if ($numFound != 0) {		foreach($results->response->docs as $entry) {			if (isset($entry->fullName_s)) {$fullname = $entry->fullName_s;}else{$fullname = "";}			$return_arr[] = $fullname;		}	}		echo json_encode($return_arr);}?>